Below is the graph of $y = a 	an (bx + c) + d$ for some constants $a,$ $b,$ $c,$ and $d.$ Find $d.$

[asy]
import TrigMacros;

size(400);

real f(real x)
{
	return 1.5*tan(0.8*(x - pi/2)) + 0.5;
}

draw(graph(f,-pi/2 + 0.1,pi/2 - 0.1,n=500,join=operator --),red);

// Vertical asymptotes
for(real k = -3; k <= 3; ++k)
{
	x = k*pi/0.8 + pi/2;
	draw((x,-5)--(x,5));
}

trig_axes(-pi/2 + 0.1,pi/2 - 0.1,-4,4,pi/2,1);

layer();
rm_trig_labels(-5,5, 2);

label("$0$", (0,0), E);
label("$1$", (0,1), E);
label("$2$", (0,2), E);
label("$-1$", (0,-1), E);
label("$-2$", (0,-2), E);
[/asy]

